DECLARE @plu varchar(max), @storeid varchar(max) SET @plu = ? SET @storeid = ?
;WITH itemselectedtable AS ( SELECT item.MdInternal, item.StoreId, CASE WHEN(LEN(item.MdName) > 0) THEN COALESCE(item.MdName, '') ELSE COALESCE(dpt.DptName, '') END MdName, CASE WHEN(LEN(item.MdNameLocal) > 0) THEN COALESCE(item.MdNameLocal, '') ELSE COALESCE(dpt.DptNameLocal, '') END MdNameLocal, item.SalesPrice1, item.Dpt, item.Line, item.Class, item.DiscountRate, item.DiscountType, item.DiscountFlag, item.DiscountAmt, item.MMCode, item.SubInt10, item.TaxType, ROW_Number() OVER (PARTITION BY item.MdInternal ORDER BY item.StoreId DESC) AS rownumber, item.AgeRestrictedFlag, item.Md32 FROM RESMaster.dbo.MST_PLU item LEFT JOIN RESMaster.dbo.MST_DPTINFO dpt ON dpt.Dpt=item.Dpt AND dpt.StoreId=item.StoreId WHERE (item.MdInternal LIKE @plu) AND (item.StoreId = @storeid OR item.StoreId = '0') )
SELECT TOP (?) MdInternal, StoreId, MdName, MdNameLocal, SalesPrice1, Dpt, Line, Class, DiscountType, DiscountRate, DiscountFlag, DiscountAmt, TaxType, MMCode, SubInt10, AgeRestrictedFlag, Md32 FROM itemselectedtable WHERE rownumber = 1 ORDER BY MdInternal ASC;

DECLARE @mdname varchar(max), @storeid varchar(max) SET @mdname = ? SET @storeid = ?
;WITH itemselectedtable AS ( SELECT MdInternal, item.StoreId, dpt.DptName, CASE WHEN(LEN(item.MdName) > 0) THEN COALESCE(item.MdName, '') ELSE COALESCE(dpt.DptName, '') END MdName, CASE WHEN(LEN(item.MdNameLocal) > 0) THEN COALESCE(item.MdNameLocal, '') ELSE COALESCE(dpt.DptNameLocal, '') END MdNameLocal, SalesPrice1, item.Dpt, Line, Class, item.DiscountType, item.DiscountRate, item.DiscountAmt, DiscountFlag, item.TaxType, item.MMCode, item.SubInt10, ROW_Number() OVER (PARTITION BY MdInternal ORDER BY item.StoreId DESC) AS rownumber, item.AgeRestrictedFlag, item.Md32 FROM RESMaster.dbo.MST_PLU item LEFT JOIN RESMaster.dbo.MST_DPTINFO dpt ON dpt.Dpt = item.Dpt AND dpt.StoreId = item.StoreId WHERE ((LOWER(MdName) LIKE '%' + LOWER(@mdname) + '%' OR LOWER(dpt.DptName) LIKE '%' + LOWER(@mdname) + '%') OR (LOWER(MdNameLocal) LIKE '%' + LOWER(@mdname) + '%' OR LOWER(dpt.DptNameLocal) LIKE '%' + LOWER(@mdname) + '%')) AND (item.StoreId = @storeid OR item.StoreId = '0') )
SELECT TOP (?) MdInternal, StoreId, MdName, MdNameLocal, SalesPrice1, Dpt, Line, Class, DiscountType, DiscountRate, DiscountFlag, DiscountAmt, TaxType, MMCode, SubInt10, AgeRestrictedFlag, Md32 FROM itemselectedtable WHERE rownumber = 1 ORDER BY MdName ASC;

