DECLARE @companyId varchar(12) ,@storeId varchar(6) ,@terminalId varchar(6) ,@container varchar(256) ,@mobileShop varchar(50) ,@resTransaction varchar(50) ,@resTabletUi varchar(50) ,@resUiConfig varchar(50)
