IF EXISTS (select container from txu_software_version where storeId=@storeId and terminalId=@terminalId and companyId=@companyId) UPDATE txu_software_version SET companyId = @companyId ,storeId = @storeId ,terminalId = @terminalId ,container = @container ,mobileShop = @mobileShop ,resTransaction = @resTransaction ,resTabletUi = @resTabletUi ,resUiConfig = @resUiConfig WHERE storeId=@storeId and terminalId=@terminalId and companyId=@companyId ELSE INSERT INTO txu_software_version ( companyId ,storeId ,terminalId ,container ,mobileShop ,resTransaction ,resTabletUi ,resUiConfig ) VALUES ( @companyId ,@storeId ,@terminalId ,@container ,@mobileShop ,@resTransaction ,@resTabletUi ,@resUiConfig );

    DECLARE
      @storeId varchar(MAX),
      @tillId varchar(MAX),
      @BusinessDayDate DATE,
      @SalesGuestCnt NUMERIC(10, 0),
      @ReturnGuestCnt NUMERIC(10, 0),
      @VoidGuestCnt NUMERIC(10, 0)

    SET @storeId=?
    SET @tillId=?
    SET @BusinessDayDate=?
    SET @SalesGuestCnt=?
    SET @ReturnGuestCnt=?
    SET @VoidGuestCnt=?

    IF EXISTS (
                 SELECT RetailStoreId
                 FROM   TXU_TOTAL_GUESTTILLDAY
                 WHERE  RetailStoreId=@storeId
                 AND    TillID=@tillId
                 AND    BusinessDayDate=@BusinessDayDate
              )
      UPDATE TXU_TOTAL_GUESTTILLDAY
      SET    SalesGuestCnt = SalesGuestCnt + @SalesGuestCnt,
             ReturnGuestCnt = ReturnGuestCnt + @ReturnGuestCnt,
             VoidGuestCnt = VoidGuestCnt + @VoidGuestCnt
      WHERE  RetailStoreId=@storeId
      AND    TillID=@tillId
      AND    BusinessDayDate=@BusinessDayDate
    ELSE
      INSERT INTO TXU_TOTAL_GUESTTILLDAY (
        RetailStoreId,
        TillID,
        BusinessDayDate,
        SalesGuestCnt,
        ReturnGuestCnt,
        VoidGuestCnt
      ) VALUES (
        @storeId,
        @tillId,
        @BusinessDayDate,
        @SalesGuestCnt,
        @ReturnGuestCnt,
        @VoidGuestCnt
      );

