    DECLARE
      @companyid varchar(MAX),
      @storeid varchar(MAX),
      @queue varchar(MAX),
      @termid varchar(MAX),
      @txid NUMERIC(5,0),
      @txdate DATE,
      @trainingflag NUMERIC(1,0)

    SET @companyid = ?;
    SET @storeid = ?;
    SET @queue = ?;
    SET @termid = ?;
    SET @txid = ?;
    SET @txdate = ?;
    SET @trainingflag = ?;

    SET TRANSACTION ISOLATION LEVEL REPEATABLE READ;

    BEGIN TRAN
    SELECT
      Tx
    FROM
      RESTransaction.dbo.TXL_INPROGRESS_ITEM WITH (UPDLOCK)
    WHERE
      CompanyId = @companyid AND
      RetailStoreId = @storeid AND
      Queue = @queue AND
      WorkstationId = @termid AND
      SequenceNumber = @txid AND
      BusinessDayDate = @txdate AND
      TrainingFlag = @trainingflag AND
      Status = 0

    UPDATE
      RESTransaction.dbo.TXL_INPROGRESS_ITEM
    SET
      Status = 1
    WHERE
      CompanyId = @companyid AND
      RetailStoreId = @storeid AND
      Queue = @queue AND
      WorkstationId = @termid AND
      SequenceNumber = @txid AND
      BusinessDayDate = @txdate AND
      TrainingFlag = @trainingflag AND
      Status = 0
    COMMIT TRAN

