    SELECT
      StoreId, TillId
    FROM
      RESMaster.dbo.MST_TILLIDINFO(UPDLOCK)
    WHERE
      CompanyId = @companyid AND
      StoreId = @storeid AND
      TillId = @tillid

    UPDATE
      RESMaster.dbo.MST_TILLIDINFO
    SET
       SodFlag = @sodflag,
       EodFlag = @eodflag,
       BusinessDayDate = @date,
       UpdOpeCode = @operatorid
    WHERE
       CompanyId = @companyid AND
       StoreId = @storeid AND
       TillId = @tillid
    COMMIT TRAN

