DECLARE @storeId VARCHAR(10) ,@companyId VARCHAR(12), @MdInternal VARCHAR(32), @businessDate VARCHAR(32) SET @storeId = LTRIM(RTRIM(?)) SET @companyId = LTRIM(RTRIM(?)) SET @MdInternal = LTRIM(RTRIM(?)) SET @businessDate = LTRIM(RTRIM(?)) SELECT TOP 1 plu.CompanyId, plu.StoreId, plu.MdInternal, plu.MdType, plu.MdVender, plu.Dpt, plu.Line, plu.Class, plu.Sku, plu.Md01, plu.Md02, plu.Md03, plu.Md04, plu.Md05, plu.Md06, plu.Md07, plu.Md08, plu.Md09, plu.Md10, plu.Md11, plu.Md12, plu.Md13, plu.Md14, plu.Md15, plu.Md16, plu.MdNameLocal, plu.MdKanaName, plu.OrgSalesPrice1, urgent.NewPrice, urgent.OldPrice, plustore.SalesPrice, plu.SalesPrice1, plu.SalesPrice2, plu.CostPrice1, plu.SalesDate, plu.MakerPrice, plu.TaxType, plu.TaxRate, plu.DiscountType, plu.PaymentType, plu.Conn1, plu.ConnType1, plu.Conn2, plu.DiscountFlag, plu.SubCode1, plu.SubCode2, plu.SubCode3, plu.SubNum1, plu.SubNum2 FROM RESMaster.dbo.MST_PLU plu LEFT JOIN RESMaster.dbo.MST_PLU_STORE plustore ON plu.CompanyId = plustore.CompanyId AND plustore.StoreId = @storeId AND plustore.MdInternal = plu.MdInternal AND plustore.DeleteFlag = 0.0 LEFT JOIN ( SELECT info.CompanyId, info.Sku, info.SequenceNo, info.ColorId, info.NewPrice, info.OldPrice FROM RESMaster.dbo.MST_PRICE_URGENT_INFO_FORSTORE info INNER JOIN RESMaster.dbo.MST_PRICE_URGENT_STORE_FORSTORE store ON info.CompanyId = store.CompanyId AND info.RecordId = store.RecordId AND store.DeleteFlag = 0.0 AND CONVERT(DATE,@businessDate) BETWEEN store.StartDate AND store.EndDate AND store.TargetStoreType IN ('0','2') OR (store.TargetStoreType = '1' AND store.StoreId = @storeId) AND info.DeleteFlag = 0
) urgent ON plu.CompanyId = urgent.CompanyId AND plu.Sku = urgent.Sku AND plu.Md01 = urgent.ColorId
WHERE plu.companyId = @companyId AND plu.MdInternal = @MdInternal AND plu.StoreId = '0' AND plu.DeleteFlag = 0.0 ORDER BY urgent.SequenceNo DESC
